import { BrowserRouter as Router, Routes, Route, NavLink, useParams, useNavigate } from "react-router-dom";
import { ShoppingCart, Heart, User, Search, X, Filter, Star, ChevronRight, Minus, Plus, Trash2 } from "lucide-react";


